import React, { useState } from 'react';
import ReactDOM from 'react-dom';

// LoginPage component
function LoginPage({ onBack }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Placeholder: handle login logic here
    alert(`Logged in as ${username}`);
    if (onBack) onBack();
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center'
    }}>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem', minWidth: 300 }}>
        <h2>Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        <div style={{ display: 'flex', gap: '1rem' }}>
          <button type="submit">Login</button>
          <button type="button" onClick={onBack}>Cancel</button>
        </div>
      </form>
    </div>
  );
}

// MainPage component for displaying tasks and projects
function MainPage({ tasks, projects }) {
  const [showCreate, setShowCreate] = useState(false);
  const [showLogin, setShowLogin] = useState(false);

  if (showLogin) {
    return <LoginPage onBack={() => setShowLogin(false)} />;
  }

  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Top-right menu */}
      <div style={{ position: 'absolute', top: 0, right: 0 }}>
        <button onClick={() => setShowLogin(true)}>Login</button>
        <button>System Settings</button>
        <button>Other Features</button>
      </div>

      {/* Centered content */}
      <div style={{
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'
      }}>
        {!showCreate ? (
          <>
            <button
              style={{
                padding: '1rem 2rem',
                fontSize: '1.5rem',
                marginBottom: '2rem',
                cursor: 'pointer'
              }}
              onClick={() => setShowCreate(true)}
            >
              New project
            </button>
            <h1>Tasks</h1>
            <ul>
              {tasks.map((task, index) => (
                <li key={index}>{task.name} - {task.status}</li>
              ))}
            </ul>
            <h1>Projects</h1>
            <ul>
              {projects.map((project, index) => (
                <li key={index}>{project.name}</li>
              ))}
            </ul>
          </>
        ) : (
          <CreateProject onBack={() => setShowCreate(false)} />
        )}
      </div>
    </div>
  );
}

// Example usage of MainPage
const exampleTasks = [
  { name: 'Subtask 1', status: 'Pending' },
  { name: 'Subtask 2', status: 'Completed' }
];

const exampleProjects = [
  { name: 'Project A' },
  { name: 'Project B' }
];

ReactDOM.render(<MainPage tasks={exampleTasks} projects={exampleProjects} />, document.getElementById('root'));

// Feature: Creating projects UI
function CreateProject({ onBack }) {
  const [project, setProject] = useState({
    name: '',
    teamMembers: [],
    deadline: '',
    tasks: []
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('/create-project', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(project)
    });
    const data = await response.json();
    alert(data.message);
    if (onBack) onBack();
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem', minWidth: 300 }}>
      <input
        type="text"
        placeholder="Project Name"
        value={project.name}
        onChange={(e) => setProject({ ...project, name: e.target.value })}
        required
      />
      {/* Inputs for team members */}
      <input
        type="text"
        placeholder="Team Member Name"
        onChange={(e) => {
          const newMember = { name: e.target.value, role: '' };
          setProject({ ...project, teamMembers: [...project.teamMembers, newMember] });
        }}
      />
      <input
        type="text"
        placeholder="Team Member Role"
        onChange={(e) => {
          const updatedMembers = [...project.teamMembers];
          if (updatedMembers.length > 0) {
            updatedMembers[updatedMembers.length - 1].role = e.target.value;
            setProject({ ...project, teamMembers: updatedMembers });
          }
        }}
      />
      {/* Input for deadline */}
      <input
        type="date"
        value={project.deadline}
        onChange={(e) => setProject({ ...project, deadline: e.target.value })}
      />
      {/* Inputs for tasks and subtasks */}
      <input
        type="text"
        placeholder="Task Name"
        onChange={(e) => {
          const newTask = { name: e.target.value, status: 'Pending', subTasks: [] };
          setProject({ ...project, tasks: [...project.tasks, newTask] });
        }}
      />
      <input
        type="text"
        placeholder="Subtask Name"
        onChange={(e) => {
          const updatedTasks = [...project.tasks];
          if (updatedTasks.length > 0) {
            updatedTasks[updatedTasks.length - 1].subTasks.push({ name: e.target.value, status: 'Pending' });
            setProject({ ...project, tasks: updatedTasks });
          }
        }}
      />
      <div style={{ display: 'flex', gap: '1rem' }}>
        <button type="submit">Create Project</button>
        <button type="button" onClick={onBack}>Cancel</button>
      </div>
    </form>
  );
}

export default CreateProject;
